mapper.select:
		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (

		select *
		from operation

		
			
			
			oper_code >  #{operCode1}
		
			
			
								
								and oper_name = #{operName}
							
			
		

		order by oper_code
		
			 				
		) A WHERE ROWNUM <= 10 )

		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (

		select itm_code,
		itm_name, itm_size
		from item

		
			
			
			itm_code >  #{itmCode1}
		
			
			
								
								and itm_name = #{itmName}
							
			



		

		order by itm_code
								
		) A WHERE ROWNUM <= 10 )

		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (

		select prc_code,
		prc_name, prc_note
		from process
		

			
								
										prc_code >  #{prcCode1}
							
			
			
								
								and prc_code = #{prcCode}
							
			


		
		order by prc_code
			
								
		) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM (
select m.mat_code, m.mat_name, m.mat_size, o.oper_name from material m, operation o where m.oper_code = o.oper_code
m.mat_code > #{matCode1}
and m.mat_code = #{matCode}
order by m.mat_code
) A WHERE ROWNUM <= 10 )

		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (

		select mac_code,
		mac_name, mac_div, mac_size
		from machine
		

			
								
								mac_code >  #{macCode1}
							
			
			
								
								and mac_code = #{macCode}
							
			
			

		
		order by mac_code
								
		) A WHERE ROWNUM <= 10 )

		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (

		select mac_code,
		mac_name, mac_div, mac_size
		from machine
		

			
								
								mac_code >  #{macCode1}
							
			
			
								
								and mac_code = #{macCode}
							
			
			

		
		order by mac_code
								
		) A WHERE ROWNUM <= 10 )
