mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM (
select * from operation
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM (
select itm_code, itm_name, itm_size from item
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM (
select prc_code, prc_name, prc_note from process
) A WHERE ROWNUM <= 10 )

		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (

		select m.mat_code, m.mat_name, m.mat_size, i.operation_name
		from material m, item i;
			
								
		) A WHERE ROWNUM <= 10 )

		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (

		select mac_code, mac_name, mac_div, mac_size
		from machine;
			
								
		) A WHERE ROWNUM <= 10 )
