mapper.select:
		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (

		select *
		from operation

		
			
			
			oper_code >  #{operCode1}
		
			
			
								
								and oper_name = #{operName}
							
			
		

		order by oper_code
		
			 				
		) A WHERE ROWNUM <= 10 )

		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (

		select itm_code,
		itm_name, itm_size
		from item

		
			
			
			itm_code >  #{itmCode1}
		
			
			
								
								and itm_name = #{itmName}
							
			



		

		order by itm_code
								
		) A WHERE ROWNUM <= 10 )

		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (

		select prc_code,
		prc_name, prc_note
		from process
		

			
								
										prc_code >  #{prcCode1}
							
			
			
								
								and prc_code = #{prcCode}
							
			


		
		order by prc_code
			
								
		) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM (
select m.mat_code, m.mat_name, m.mat_size, o.oper_name from material m, operation o where m.oper_code = o.oper_code
m.mat_code > #{matCode1}
and m.mat_code = #{matCode}
order by m.mat_code
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM (
select m.*, to_char(m.buy_date,'yyyy-MM-dd') as buy_date_1, p.prc_name, a.prc_code from MACHINE m ,process p, machine_process a where p.prc_code = a.prc_code and m.mac_code = a.mac_code
m.mac_code > #{macCode1}
and m.mac_code = #{macCode}
order by m.mac_code
) A WHERE ROWNUM <= 10 )

		SELECT * FROM (
		SELECT A.*, ROWNUM RNUM FROM (


		select emp_id, emp_name, department, position
		from employees

		

			
								
								emp_id >  #{empId1}
							
			
			
								
								and emp_name = #{empName}
							
			


		
		order by emp_id
								
		) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM (
select m.mac_code , m.mac_name from process_result_d prd , machine_process mp , machine m where prd.prc_code = mp.prc_code and prd.mac_code is null and m.mac_code=mp.mac_code and mp.mac_code not in(select mac_code from process_result_d where prc_state = '진행' and prc_com_d_vol >0 and mac_code is not null) and prc_res_d_num = #{prcResDNum}
order by mac_code
) A WHERE ROWNUM <= 10 )
