mapper.insert:
begin 		
INSERT INTO MACHINE 
		( MAC_CODE
		  , MAC_NAME
		  , MAC_DIV
		  , USE_1
		  , MODEL
		  , PRO_OPER
		  , WORKER
		  , USE
		  , USING_ENERGY
		  , BUY_DATE
		  , BUY_PRICE
		  , MAC_LOADAGE
		  , MAC_STD_TEMP
		  , image_path )
	VALUES ( 
			#{macCode}
		  , #{macName}
		  , #{macDiv}
		  , #{use1}
		  , #{model}
		  , #{proOper}
		  , #{worker}
		  , #{use}
		  , #{usingEnergy}
		  , to_date(#{buyDate1}, 'yyyy-MM-dd')
		  , #{buyPrice}
		  , #{macLoadage}
		  , #{macStdTemp}
		  , #{imagePath}
		  );
insert into machine_process VALUES (#{macCode}, #{prcCode}, (select max(prc_mac_num)+1 from machine_process));
end;

mapper.update:

UPDATE MACHINE
SET 
	USE_1=#{use1}
	, WORKER=#{worker}
	, USE=#{use}
	, USING_ENERGY=#{usingEnergy}
	, MAC_LOADAGE=#{macLoadage}
	, MAC_STD_TEMP=#{macStdTemp}
	WHERE MAC_CODE=#{macCode}

mapper.delete:

begin delete from machine_process
where mac_code = #{macCode};

delete from machine
where mac_code = #{macCode};
end;
