mapper.insert:

INSERT INTO MACHINE 
	( MAC_CODE
	  , MAC_NAME
	  , MAC_DIV
	 , USE_1
	  , MODEL
	  , PRO_OPER
	  , WORKER
	  , USE
	  , USING_ENERGY
	  , BUY_DATE
	  , BUY_PRICE
	  , MAC_LOADAGE
	  , MAC_STD_TEMP )
VALUES ( #{macCode}
	  , #{macName}
	  , #{macDiv}
	  , #{use1}
	  , #{model}
	  , #{proOper}
	  , #{worker}
	  , #{use}
	  , #{usingEnergy}
	  , #{buyDate}
	  , #{buyPrice}
	  , #{macLoadage}
	  , #{macStdTemp} )

mapper.update:

UPDATE MACHINE
SET 
	USE_1=#{use1}
	, WORKER=#{worker}
	, USE=#{use}
	, USING_ENERGY=#{usingEnergy}
	, MAC_LOADAGE=#{macLoadage}
	, MAC_STD_TEMP=#{macStdTemp}
	WHERE MAC_CODE=#{macCode}

mapper.delete:

begin delete from machine_process
where mac_code = #{macCode};

delete from machine
where mac_code = #{macCode};
end;

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT MAC_CODE , MAC_NAME , MAC_DIV , MAC_SIZE , Worker , MODEL , PRO_OPER , USE , NOM_CAP , USING_ENERGY , SAFETY , MAC_PRO_DATE , BUY_DATE , BUY_PRICE , LINE_NUM , MAC_LOADAGE , MAC_STD_TEMP
FROM MACHINE ORDER BY MAC_CODE DESC
) A WHERE ROWNUM <= 10 )
