mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select m.mat_code, m.mat_name, m.mat_size, o.oper_name from material m, operation o where m.oper_code = o.oper_code
m.mat_code > #{matCode3}
and m.mat_code = #{matCode}
order by m.mat_code
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT TO_CHAR(MH.MAT_HIS_DATE, 'yyyy-MM-dd') AS MAT_HIS_DATE, M.MAT_CODE, M.MAT_NAME, M.MAT_SIZE, MH.MAT_ORD_OPER, MHD.MAT_HIS_D_NUM, MHD.MAT_COM_NUM, MHD.MAT_HIS_D_VOL, MHD.MAT_HIS_PRICE, (MHD.MAT_HIS_D_VOL * MHD.MAT_HIS_PRICE) AS AMOUNT, MHD.LOT_Num, MHD.MAT_HIS_NUM FROM MATERIAL M, MATERIAL_HISTORY_D MHD, MATERIAL_HISTORY MH, OPERATION O WHERE M.MAT_CODE = MHD.MAT_CODE AND MH.MAT_HIS_NUM = MHD.MAT_HIS_NUM AND M.OPER_CODE = O.OPER_CODE AND MH.MAT_HIS_DIV='입고'
and mhd.mat_his_num = #{matHisNum}
and MHD.MAT_HIS_D_NUM < #{matHisDNum1}
AND
MH.MAT_HIS_DATE >= TO_DATE(#{matHisDateS} ,'yyyy-MM-dd') and MH.MAT_HIS_DATE <= TO_DATE(#{matHisDateE} ,'yyyy-MM-dd')
AND M.MAT_CODE IN
#{item}
AND O.OPER_CODE IN (${operCode})
order by MHD.MAT_HIS_D_NUM desc
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT B.*, C.MAT_VOL, NVL(D.MAT_NORD_VOL,0) FROM (SELECT TO_CHAR(MH.MAT_HIS_DATE, 'yyyy-MM-dd') as MAT_HIS_DATE, M.MAT_CODE, M.MAT_NAME, M.MAT_SIZE, M.MAT_UNIT, MHD.MAT_COM_NUM, MHD.MAT_HIS_D_VOL, MHD.MAT_HIS_PRICE, (MHD.MAT_HIS_D_VOL * MHD.MAT_HIS_PRICE) AS AMOUNT, MHD.LOT_Num, MHD.MAT_HIS_NUM, MHD.MAT_HIS_D_NUM FROM MATERIAL M , MATERIAL_HISTORY_D MHD , MATERIAL_HISTORY MH WHERE M.MAT_CODE = MHD.MAT_CODE AND MH.MAT_HIS_NUM = MHD.MAT_HIS_NUM AND MH.MAT_HIS_DIV='입고'
AND MHD.MAT_HIS_NUM = #{matHisNum}
AND MHD.MAT_HIS_D_NUM > #{matHisDNum1}
AND
MH.MAT_HIS_DATE >= TO_DATE(#{sDate} ,'yyyy-MM-dd') and MH.MAT_HIS_DATE <= TO_DATE(#{eDate} ,'yyyy-MM-dd')
AND O.OPER_CODE IN
#{item}
) B LEFT OUTER JOIN (SELECT MAT_CODE, SUM(MAT_VOL) AS MAT_VOL FROM MATERIAL_STOCK GROUP BY MAT_CODE) C ON B.MAT_CODE = C.MAT_CODE LEFT OUTER JOIN (SELECT MAT_CODE, SUM(MAT_NORD_VOL) AS MAT_NORD_VOL FROM MATERIAL_ORDER GROUP BY MAT_CODE) D ON C.MAT_CODE = D.MAT_CODE ORDER BY B.MAT_CODE
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT B.*, C.MAT_VOL FROM(SELECT MO.MAT_COM_NUM , MO.MAT_NORD_VOL , MO.MAT_CODE , MO.MAT_ORD_OPER , M.MAT_NAME , M.MAT_SIZE , M.MAT_UNIT FROM MATERIAL_ORDER MO , MATERIAL M WHERE M.MAT_CODE = MO.MAT_CODE AND MO.MAT_NORD_VOL > 0 AND MO.MAT_ORD_OPER = #{operName} AND
MO.MAT_ORD_DATE >= TO_DATE(#{matComDateS} ,'yyyy-MM-dd') and MO.MAT_ORD_DATE <= TO_DATE(#{matComDateE} ,'yyyy-MM-dd')
) B LEFT OUTER JOIN (SELECT MAT_CODE, SUM(MAT_VOL) AS MAT_VOL FROM MATERIAL_STOCK GROUP BY MAT_CODE) C ON B.MAT_CODE = C.MAT_CODE ORDER BY B.MAT_CODE
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT MAT_ORD_NUM , MAT_COM_NUM , TO_CHAR(MAT_ORD_DATE, 'yyyy-MM-dd') AS MAT_ORD_DATE , MAT_ORD_OPER , MAT_CODE , MAT_NORD_VOL FROM MATERIAL_ORDER WHERE MAT_NORD_VOL > 0
and MAT_ORD_NUM > #{matOrdNum1}
AND
MAT_ORD_DATE >= TO_DATE(#{nsDate} ,'yyyy-MM-dd') and MAT_ORD_DATE <= TO_DATE(#{neDate} ,'yyyy-MM-dd')
ORDER BY MAT_ORD_DATE
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT TO_CHAR(MH.MAT_HIS_DATE, 'yyyy-MM-dd') as MAT_HIS_DATE, M.MAT_CODE, M.MAT_NAME, M.MAT_SIZE, M.MAT_UNIT, MHD.MAT_COM_NUM, MHD.MAT_HIS_D_VOL, MHD.MAT_HIS_PRICE, (MHD.MAT_HIS_D_VOL * MHD.MAT_HIS_PRICE) AS AMOUNT, MHD.LOT_NUM, MHD.MAT_HIS_NUM, MHD.MAT_HIS_D_NUM, MH.MAT_ORD_OPER, O.OPER_CODE, MS.MAT_VOL, MO.MAT_NORD_VOL, MHD.MAT_ORD_NUM FROM MATERIAL M , MATERIAL_HISTORY_D MHD , MATERIAL_HISTORY MH , OPERATION O , MATERIAL_STOCK MS , MATERIAL_ORDER MO WHERE M.MAT_CODE = MHD.MAT_CODE AND MH.MAT_HIS_NUM = MHD.MAT_HIS_NUM AND MHD.LOT_NUM = MS.LOT_NUM AND M.OPER_CODE = O.OPER_CODE AND MHD.MAT_ORD_NUM = MO.MAT_ORD_NUM AND MH.MAT_HIS_DIV='입고' AND MO.MAT_NORD_VOL > 0
and mh.mat_ord_oper = #{operName}
and mhd.mat_his_d_num > #{matHisDNum1}
AND
MO.MAT_ORD_DATE >= TO_DATE(#{matComDateS} ,'yyyy-MM-dd') and MO.MAT_ORD_DATE <= TO_DATE(#{matComDateE} ,'yyyy-MM-dd')
ORDER BY MHD.MAT_HIS_D_NUM ) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT TO_CHAR(MH.MAT_HIS_DATE, 'yyyy-MM-dd') AS MAT_HIS_DATE, M.MAT_CODE, M.MAT_NAME, M.MAT_SIZE, MH.MAT_OUT, MHD.MAT_HIS_D_NUM, MHD.MAT_HIS_D_VOL, MHD.MAT_HIS_PRICE, (MHD.MAT_HIS_D_VOL * MHD.MAT_HIS_PRICE) AS AMOUNT, MHD.LOT_Num, MHD.MAT_HIS_NUM FROM MATERIAL M, MATERIAL_HISTORY_D MHD, MATERIAL_HISTORY MH, PROCESS P WHERE M.MAT_CODE = MHD.MAT_CODE AND MH.MAT_HIS_NUM = MHD.MAT_HIS_NUM AND P.PRC_NAME = MH.MAT_OUT AND MH.MAT_HIS_DIV='출고'
AND MH.MAT_OUT = #{matOut}
and mhd.mat_his_num = #{matHisNum}
and MHD.MAT_HIS_D_NUM < #{matHisDNum1}
AND
MH.MAT_HIS_DATE >= TO_DATE(#{matHisDateS} ,'yyyy-MM-dd') and MH.MAT_HIS_DATE <= TO_DATE(#{matHisDateE} ,'yyyy-MM-dd')
AND M.MAT_CODE IN
#{item}
order by MHD.MAT_HIS_D_NUM desc
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT TO_CHAR(MH.MAT_HIS_DATE, 'yyyy-MM-DD') as MAT_HIS_DATE, M.MAT_CODE, M.MAT_NAME, M.MAT_SIZE, MH.MAT_OUT, P.PRC_CODE, M.MAT_UNIT, MHD.MAT_HIS_D_VOL, MHD.LOT_Num, MS.MAT_VOL, MHD.MAT_HIS_NUM, MHD.MAT_HIS_D_NUM FROM MATERIAL M, MATERIAL_HISTORY_D MHD, MATERIAL_STOCK MS, MATERIAL_HISTORY MH, PROCESS P WHERE M.MAT_CODE = MHD.MAT_CODE AND M.MAT_CODE = MS.MAT_CODE AND MH.MAT_HIS_NUM = MHD.MAT_HIS_NUM AND P.PRC_NAME = MH.MAT_OUT AND MH.MAT_HIS_DIV='출고'
and mh.mat_His_Num = #{matHisNum}
and mhd.mat_his_d_num > #{matHisDNum1}
AND
MH.MAT_HIS_DATE >= TO_DATE(#{sDate} ,'yyyy-MM-dd') and MH.MAT_HIS_DATE <= TO_DATE(#{eDate} ,'yyyy-MM-dd')
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT TO_CHAR(mh.MAT_HIS_DATE, 'yyyy-MM-DD') as MAT_HIS_DATE , MH.MAT_HIS_NUM , MH.MAT_HIS_VOL , MH.MAT_ORD_OPER , O.OPER_CODE FROM MATERIAL_HISTORY MH , OPERATION O WHERE MH.MAT_ORD_OPER = O.OPER_NAME AND MH.MAT_HIS_DIV = '입고' AND mat_his_num = #{matHisNum}
AND mat_his_num > #{matHisNum1}
AND
MAT_HIS_DATE >= TO_DATE(#{sDate} ,'yyyy-MM-dd') and MAT_HIS_DATE <= TO_DATE(#{eDate} ,'yyyy-MM-dd')
order by mat_his_num
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT TO_CHAR(mh.MAT_HIS_DATE, 'yyyy-MM-DD') as MAT_HIS_DATE , MH.MAT_HIS_NUM , MH.MAT_OUT , MH.MAT_HIS_VOL , P.PRC_CODE FROM MATERIAL_HISTORY MH , PROCESS P WHERE MH.MAT_HIS_DIV = '출고' AND MH.MAT_OUT = P.PRC_NAME AND mat_his_num = #{matHisNum}
and mh.mat_his_num > #{matHisNum1}
