mapper.insert:

	INSERT INTO MATERIAL_HISTORY 
		( MAT_HIS_NUM
		  , MAT_HIS_DIV
		  , MAT_HIS_DATE
		  , MAT_OUT
		  , MAT_HIS_VOL )
	VALUES ( #{matHisNum}
		  , #{matHisDiv}
		  , #{matHisDate}
		  , #{matOut}
		  , #{matHisVol} )

mapper.update:

	UPDATE MATERIAL_HISTORY
	SET MAT_HIS_NUM=#{matHisNum}
		, MAT_HIS_DIV=#{matHisDiv}
		, MAT_HIS_DATE=#{matHisDate}
		, MAT_OUT=#{matOut}
		, MAT_HIS_VOL=#{matHisVol}
				WHERE MAT_HIS_NUM=#{matHisNum}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT MAT_HIS_NUM , MAT_HIS_DIV , MAT_HIS_DATE , MAT_OUT , MAT_HIS_VOL FROM MATERIAL_HISTORY WHERE 1=1
AND MAT_HIS_NUM = #{searchKeyword}
AND MAT_HIS_DIV LIKE '%' || #{searchKeyword} || '%'
ORDER BY MAT_HIS_NUM DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM > #{firstIndex}

SELECT COUNT(*) totcnt FROM MATERIAL_HISTORY WHERE 1=1
AND MAT_HIS_NUM = #{searchKeyword}
AND MAT_HIS_DIV LIKE '%' || #{searchKeyword} || '%'
