mapper.update:

	UPDATE MATERIAL_STOCK
	SET LOT_NUM=#{lotNum}
		, MAT_CODE=#{matCode}
		, MAT_VOL=#{matVol}
				WHERE LOT_NUM=#{lotNum}
						AND MAT_CODE=#{matCode}

mapper.delete:

	DELETE FROM MATERIAL_STOCK 
				WHERE LOT_NUM=#{lotNum}
						AND MAT_CODE=#{matCode}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT M.MAT_CODE, M.MAT_NAME, M.MAT_DIV, SA.MAT_SAFE_STOCK, ST.MAT_VOL, (SA.MAT_SAFE_STOCK - ST.MAT_VOL) AS SHORT, MHD.MAT_HIS_D_VOL FROM MATERIAL M, MATERIAL_SAFE SA, MATERIAL_STOCK ST, MATERIAL_HISTORY MH, MATERIAL_HISTORY_D MHD WHERE M.MAT_CODE=SA.MAT_CODE AND M.MAT_CODE=MHD.MAT_CODE AND M.MAT_CODE=ST.MAT_CODE AND MH.MAT_HIS_NUM=MHD.MAT_HIS_NUM AND MH.MAT_HIS_DIV='입고' and m.mat_code between #{matCode} and #{matCode} AND MH.MAT_HIS_DATE BETWEEN TO_DATE(#{matHisDate}, 'YYYY-MM-DD') AND TO_DATE(#{matHisDate}, 'YYYY-MM-DD')
AND M.MAT_NAME = #{searchKeyword}
AND M.MAT_CODE LIKE '%' || #{searchKeyword} || '%'
ORDER BY M.MAT_CODE DESC
) A WHERE ROWNUM <= 10 )

SELECT COUNT(*) totcnt FROM MATERIAL_STOCK WHERE 1=1
AND LOT_NUM = #{searchKeyword}
AND MAT_CODE LIKE '%' || #{searchKeyword} || '%'

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT M.MAT_CODE, M.MAT_NAME, M.MAT_SIZE, M.MAT_UNIT, MHD.LOT_NO, MHD.MAT_HIS_D_VOL, ST.MAT_VOL FROM MATERIAL M, MATERIAL_STOCK ST, MATERIAL_HISTORY_D MHD WHERE M.MAT_CODE=MHD.MAT_CODE AND M.MAT_CODE=ST.MAT_CODE
AND M.MAT_NAME = #{searchKeyword}
AND M.MAT_CODE LIKE '%' || #{searchKeyword} || '%'
ORDER BY M.MAT_CODE DESC
) A WHERE ROWNUM <= 10 )
