mapper.select:
SELECT * FROM ( SELECT d.*, ROWNUM RNUM FROM ( SELECT M.MAT_NAME, m.mat_size, m.mat_div, A.*, NVL(ms.mat_vol,0) AS MAT_VOL FROM (select a.mat_code, a.lot_num, (a.allvol - nvl(b.outvol,0)) as inVOL, nvl(b.outvol,0) AS OUTVOL from(select mat_code,nvl(lot_num,'null') as lot_num, nvl(sum(mat_his_d_vol),0) as allvol from material_history_d mhd, material_history mh where mhd.mat_his_num = mh.mat_his_num
AND mh.MAT_HIS_DATE >= TO_DATE(#{sDate} ,'yyyy-MM-dd') and mh.MAT_HIS_DATE <= TO_DATE(#{eDate} ,'yyyy-MM-dd')
group by mat_code, lot_num) a ,(select mat_code, nvl(lot_num,'null') as lot_num, nvl(sum(mat_his_d_vol),0) as outvol from material_history_d mhd, material_history mh where mhd.mat_his_num = mh.mat_his_num and mh.mat_his_num in (select mat_his_num from material_history where mat_his_div = '출고')
AND mh.MAT_HIS_DATE >= TO_DATE(#{sDate} ,'yyyy-MM-dd') and mh.MAT_HIS_DATE <= TO_DATE(#{eDate} ,'yyyy-MM-dd')
group by mat_code, lot_num)b where a.mat_code = b.mat_code (+) and a.lot_num = b.lot_num (+)) A, MATERIAL_STOCK MS, MATERIAL M WHERE A.MAT_CODE = MS.MAT_CODE (+) AND A.LOT_NUM = MS.LOT_NUM (+) AND A.MAT_CODE = M.MAT_CODE
AND M.MAT_CODE > #{matCode3}
AND M.MAT_CODE IN
#{item}
ORDER BY A.MAT_CODE, A.LOT_NUM
) d WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT B.*, C.OUTVOL, (B.INVOL - C.OUTVOL) AS MAT_VOL, (B.MAT_SAFE_STOCK - (B.INVOL - C.OUTVOL)) AS MSHORT FROM (SELECT MHD.MAT_CODE , SUM(MHD.MAT_HIS_D_VOL) AS INVOL , M.MAT_NAME , M.MAT_DIV , SA.MAT_SAFE_STOCK FROM MATERIAL M , MATERIAL_HISTORY_D MHD , MATERIAL_HISTORY MH , MATERIAL_SAFE SA WHERE MHD.MAT_HIS_NUM = MH.MAT_HIS_NUM AND M.MAT_CODE = MHD.MAT_CODE AND SA.MAT_CODE = M.MAT_CODE AND MH.MAT_HIS_DIV = '입고'
AND MH.MAT_HIS_DATE >= TO_DATE(#{sDate} ,'yyyy-MM-dd') AND MH.MAT_HIS_DATE <= TO_DATE(#{eDate} ,'yyyy-MM-dd')
AND M.MAT_CODE IN
#{item}
AND MHD.MAT_CODE > #{matCode3}
GROUP BY MHD.MAT_CODE , M.MAT_NAME , M.MAT_DIV , SA.MAT_SAFE_STOCK ) B LEFT OUTER JOIN (SELECT MAT_CODE, SUM(MAT_HIS_D_VOL) AS OUTVOL FROM MATERIAL_HISTORY_D MHD, MATERIAL_HISTORY MH WHERE MHD.MAT_HIS_NUM = MH.MAT_HIS_NUM AND MH.MAT_HIS_DIV = '출고'
AND MH.MAT_HIS_DATE >= TO_DATE(#{sDate} ,'yyyy-MM-dd') AND MH.MAT_HIS_DATE <= TO_DATE(#{eDate} ,'yyyy-MM-dd')
AND MHD.MAT_CODE IN
#{item}
AND MHD.MAT_CODE > #{matCode3}
GROUP BY MAT_CODE) C ON B.MAT_CODE = C.MAT_CODE ORDER BY B.MAT_CODE
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT M.MAT_CODE, M.MAT_NAME, M.MAT_SIZE, M.MAT_UNIT, MHD.LOT_NUM, MHD.MAT_HIS_D_VOL, ST.MAT_VOL FROM MATERIAL M, MATERIAL_STOCK ST, MATERIAL_HISTORY_D MHD WHERE M.MAT_CODE=MHD.MAT_CODE AND M.MAT_CODE=ST.MAT_CODE
AND M.MAT_NAME = #{searchKeyword}
AND M.MAT_CODE LIKE '%' || #{searchKeyword} || '%'
ORDER BY M.MAT_CODE DESC
) A WHERE ROWNUM <= 10 )
