mapper.insert:

	INSERT INTO PRODUCE_COMMAND_D 
		( PRD_COM_D_NUM
		  , PRD_COM_NUM
		  , MAT_CODE
		  , LOT_NUM
		  , ITM_CODE
		  , PRC_F_NO
		  , PRD_COM_VOL
		  , PRD_COM_D_DATE
		  , PRC_COM_NO
		  , PRC_COM_DIV
		  , MAT_VOL
		  , PRD_COM_NOTE
		  , DIV_MAT_CODE
		  , DIV_LOT_NUM
		  , PRD_PLAN_NUM )
	VALUES ( #{prdComDNum}
		  , #{prdComNum}
		  , #{matCode}
		  , #{lotNum}
		  , #{itmCode}
		  , #{prcFNo}
		  , #{prdComVol}
		  , #{prdComDDate}
		  , #{prcComNo}
		  , #{prcComDiv}
		  , #{matVol}
		  , #{prdComNote}
		  , #{divMatCode}
		  , #{divLotNum}
		  , #{prdPlanNum} )

mapper.update:

	UPDATE PRODUCE_COMMAND_D
	SET PRD_COM_D_NUM=#{prdComDNum}
		, PRD_COM_NUM=#{prdComNum}
		, MAT_CODE=#{matCode}
		, LOT_NUM=#{lotNum}
		, ITM_CODE=#{itmCode}
		, PRC_F_NO=#{prcFNo}
		, PRD_COM_VOL=#{prdComVol}
		, PRD_COM_D_DATE=#{prdComDDate}
		, PRC_COM_NO=#{prcComNo}
		, PRC_COM_DIV=#{prcComDiv}
		, MAT_VOL=#{matVol}
		, PRD_COM_NOTE=#{prdComNote}
		, DIV_MAT_CODE=#{divMatCode}
		, DIV_LOT_NUM=#{divLotNum}
		, PRD_PLAN_NUM=#{prdPlanNum}
				WHERE PRD_COM_D_NUM=#{prdComDNum}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select prd_Com_Num , TO_CHAR(prd_Com_Date, 'yyyy/MM/dd') as prd_Com_Date, prd_Com_Name, prd_Com_Note from PRODUCE_COMMAND
Prd_Com_Num < #{prdComNum1}
ORDER BY PRD_COM_NUM DESC
) A WHERE ROWNUM <= 10)

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select A.* ,(B.yes_Com_Vol - a.prd_com_vol) as yes_Com_Vol, a.ord_vol - (B.yes_Com_Vol - a.prd_com_vol) - a.ord_out_vol as no_Com_Vol, a.itm_day_output/8 as UPH, a.prd_com_vol/a.itm_day_output as day_Num from(select C.*, I.itm_name, M.mat_name, OD.ord_num, OM.ord_delivery_date, OM.oper_name, OD.ord_vol, OD.ord_out_vol, I.itm_day_output from PRODUCE_COMMAND_d C, item I, order_d OD, order_m OM, material M where c.itm_code = i.itm_code and i.mat_code = m.mat_code and c.ord_d_num = od.ord_d_num (+) and od.ord_num = om.ord_num (+)) A, (select ord_d_num, sum(prd_com_vol) as yes_Com_Vol from produce_command_d group by ord_d_num) B where a.ord_d_num = b.ord_d_num
and Prd_Com_D_Num > #{prdComDNum1}
ORDER BY PRD_COM_D_NUM
) A WHERE ROWNUM <= 10)

SELECT * FROM (
SELECT A.*, ROWNUM RNUM FROM (
select *
from PRODUCE_COMMAND_MAT
where prd_com_d_num = #{prdComDNum}

			
			and Prd_Com_Mat_Num > #{prdComMatNum}
		

ORDER BY
PRD_COM_MAT_NUM

					
) A WHERE ROWNUM <= 10)

SELECT * FROM (
SELECT A.*, ROWNUM RNUM FROM (
select f.*, p.prc_name
from process_flow F, process P
where f.prc_code = p.prc_code
and itm_code = #{itmCode}

		
		and prc_f_no > #{prcFNo}
	

ORDER BY prc_f_no

					
) A WHERE ROWNUM <= 10)
