mapper.select:
SELECT TO_CHAR(PRD_COM_D_DATE, 'yyyy-MM-dd') PRD_COM_D_DATE , PRD_COM_NUM , (SELECT OPER_NAME FROM ORDER_D OD, ORDER_M OM WHERE OD.ORD_NUM = OM.ORD_NUM AND PCD.ORD_D_NUM = OD.ORD_D_NUM) OPER_NAME , ITM_CODE , (SELECT ITM_NAME FROM ITEM WHERE ITM_CODE = PCD.ITM_CODE) ITM_NAME , (SELECT ORD_NUM FROM ORDER_D WHERE ORD_D_NUM = PCD.ORD_D_NUM) ORD_NUM , TO_CHAR((SELECT ORD_DELIVERY_DATE FROM ORDER_M OM INNER JOIN ORDER_D OD ON OM.ORD_NUM = OD.ORD_NUM WHERE PCD.ORD_D_NUM = OD.ORD_D_NUM), 'yyyy-MM-dd') ORD_DELIVERY_DATE , (SELECT ORD_VOL FROM ORDER_D WHERE ORD_D_NUM = PCD.ORD_D_NUM) ORD_VOL , PRD_COM_VOL , PRC_COM_DIV , LOT_NUM , MAT_VOL , PRC_COM_NO , PRD_COM_D_NOTE FROM PRODUCE_COMMAND_MAT PCM, PRODUCE_COMMAND_D PCD WHERE PCM.PRD_COM_D_NUM = PCD.PRD_COM_D_NUM
AND PRD_COM_D_DATE >= #{startDate} AND PRD_COM_D_DATE <= #{endDate}
AND ITM_CODE IN
#{item}
AND (SELECT OPER_CODE FROM ORDER_D OD, ORDER_M OM WHERE OD.ORD_NUM = OM.ORD_NUM AND PCD.ORD_D_NUM = OD.ORD_D_NUM) IN
#{operCode}

SELECT * FROM (
	SELECT A.*, ROWNUM RNUM FROM (
		select itm_code, itm_name from item
		
			
					
					Prd_Com_Num > #{itmCode1}
				
			 
		
		ORDER BY itm_code
					
) A WHERE ROWNUM <= 10)

SELECT * FROM (
SELECT A.*, ROWNUM RNUM FROM (
select prd_Com_Num , TO_CHAR(prd_Com_Date, 'yyyy-MM-dd') as prd_Com_Date, prd_Com_Name, prd_Com_Note from PRODUCE_COMMAND


			
			Prd_Com_Num < #{prdComNum1}
		



	AND to_char(PRD_COM_DATE,'YYYY-MM-DD') >= #{startDate}
	AND to_char(PRD_COM_DATE,'YYYY-MM-DD') <= #{endDate}
	


ORDER BY
PRD_COM_NUM DESC

					
) A WHERE ROWNUM <= 10)

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select A.*, to_char(a.prd_com_d_date,'YYYY-MM-DD') as prd_com_d_date1, M.MAT_NAME, nvl(B.yes_Com_Vol,0) as yes_Com_Vol, a.ord_vol - nvl(B.yes_Com_Vol,0) - NVL(a.ord_out_vol,0) as no_Com_Vol, a.itm_day_output/8 as UPH, a.prd_com_vol/a.itm_day_output as day_Num from(SELECT A.PRD_DATE , (SELECT OPER_NAME FROM ORDER_M WHERE ORD_NUM = B.ORD_NUM) as OPER_NAME , B.prd_plan_d_num , B.ITM_CODE , (SELECT ITM_NAME FROM ITEM WHERE ITM_CODE = B.ITM_CODE) as ITM_NAME , (SELECT MAT_CODE FROM ITEM WHERE ITM_CODE = B.ITM_CODE) as MAT_CODE , (SELECT ITM_DAY_OUTPUT FROM ITEM WHERE ITM_CODE = B.ITM_CODE) as ITM_DAY_OUTPUT , B.ORD_NUM , B.ord_d_num , to_char((SELECT ORD_DELIVERY_DATE FROM ORDER_M WHERE ORD_NUM = B.ORD_NUM),'YYYY-MM-DD') as ORD_DELIVERY_DATE , (SELECT ORD_VOL FROM ORDER_D WHERE ORD_NUM = B.ORD_NUM AND ORD_D_NUM = B.ORD_D_NUM) ORD_VOL , (SELECT ORD_OUT_VOL FROM ORDER_D WHERE ORD_NUM = B.ORD_NUM AND ORD_D_NUM = B.ORD_D_NUM) ORD_OUT_VOL , B.PRD_PLAN_DATE as prd_com_d_date , B.PRD_NO as prc_com_no , B.prd_work_vol as prd_com_vol FROM PRODUCE_PLAN A , PRODUCE_PLAN_D B WHERE A.PRD_NUM = B.PRD_NUM and B.com_chk = 'N') A, (select ord_d_num, NVL(sum(prd_com_vol),0) as yes_Com_Vol from produce_command_d group by ord_d_num) B, MATERIAL M where a.ord_d_num = b.ord_d_num (+) AND A.MAT_CODE = M.MAT_CODE
AND to_char(A.prd_com_d_date,'YYYY-MM-DD') >= #{prdDateS} AND to_char(A.prd_com_d_date,'YYYY-MM-DD') <= #{prdDateE}
and prd_plan_d_num > #{prdPlnDNum1}
ORDER BY prd_plan_d_num
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select A.* , B.yes_Com_Vol, a.ord_vol - B.yes_Com_Vol - a.ord_out_vol as no_Com_Vol, a.itm_day_output/8 as UPH, a.prd_com_vol/a.itm_day_output as day_Num from(select C.*, TO_CHAR(C.prd_com_d_date, 'yyyy-MM-dd') as prd_com_d_date1, I.itm_name, M.mat_name, OD.ord_num, to_char(OM.ord_delivery_date,'yyyy-MM-dd') ord_delivery_date, OM.oper_name, OD.ord_vol, OD.ord_out_vol, I.itm_day_output , TO_CHAR((select prd_com_date from produce_command where prd_com_num = C.prd_com_num), 'yyyy-MM-dd') prd_com_date from PRODUCE_COMMAND_d C, item I, order_d OD, order_m OM, material M where c.itm_code = i.itm_code
and c.prd_com_num = #{prdComNum}
and i.mat_code = m.mat_code and c.ord_d_num = od.ord_d_num (+) and od.ord_num = om.ord_num (+)) A, (select ord_d_num, sum(prd_com_vol) as yes_Com_Vol from produce_command_d group by ord_d_num) B where a.ord_d_num = b.ord_d_num (+)
and Prd_Com_D_Num > #{prdComDNum1}
and prd_com_d_date1 >= #{startDate} and prd_com_d_date1 <= #{endDate}
ORDER BY PRD_COM_D_NUM
) A WHERE ROWNUM <= 10)

SELECT * FROM (
SELECT A.*, ROWNUM RNUM FROM (
select *
from PRODUCE_COMMAND_MAT
where prd_com_d_num = #{prdComDNum}

		
		and pcm.Prd_Com_Mat_Num > #{prdComMatNum}
	

ORDER BY
PRD_COM_MAT_NUM

					
) A WHERE ROWNUM <= 10)

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select pcm.*, pr.mov_num from PRODUCE_COMMAND_MAT pcm, process_result pr where pcm.prd_com_d_num = pr.prd_com_d_num and pcm.prd_com_d_num = #{prdComDNum} and pcm.lot_num = pr.lot_num
and pcm.Prd_Com_Mat_Num > #{prdComMatNum}
ORDER BY PRD_COM_MAT_NUM
) A WHERE ROWNUM <= 10)

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select a.lot_num, a.mat_code, (a.mat_vol - nvl(b.mat_vol,0)) as mat_vol from material_stock a, (select lot_num, sum(mat_vol) as mat_vol from produce_command_mat where prd_com_d_num in (select prd_com_d_num from produce_command_d where mat_out_chk = 'N') group by lot_num) b where a.lot_num = b.lot_num (+) and mat_code = #{matCode}
and lot_Num > #{lotNum1}
ORDER BY lot_Num
) A WHERE ROWNUM <= 10)

SELECT * FROM (
SELECT A.*, ROWNUM RNUM FROM (
select f.*, p.prc_name

,NVL((select prc_state from process_result_d where mov_num = #{movNum} and prc_code = f.prc_code), '완료') AS PRC_STATE

from process_flow F, process P
where f.prc_code = p.prc_code
and itm_code = #{itmCode}

		
		and prc_f_no > #{prcFNo}
	

ORDER BY prc_f_no

					
) A WHERE ROWNUM <= 10)

mapper.update:
update Produce_Command set 
	prd_com_date = to_date(#{prdComDate},'yyyy-MM-dd'),
	prd_com_name = #{prdComName},
	prd_com_note = #{prdComNote}
where prd_com_num = #{prdComNum}

mapper.insert:
declare
v_matCode item.mat_code%type;
v_prdWorkVol produce_plan_d.prd_work_vol%type;
v_prdComVol produce_command_d.prd_com_vol%type;
v_ordIndVol order_d.ord_ind_vol%type;
begin
	select mat_code into v_matCode
	from item
	where itm_code = #{itmCode};

	insert into Produce_Command_D
		(prd_plan_d_num, ord_d_num, prd_com_d_num, prd_com_num, itm_code, mat_code, prd_com_vol, prd_com_d_date, prc_com_no, prc_com_div, prd_com_d_note) 
		values(
			#{prdPlanDNum},
			#{ordDNum},
			produce_command_d_seq.nextval,
			#{prdComNum},
			#{itmCode},
			v_matCode,
			#{prdComVol},
			to_date(#{prdComDDate1},'YYYY-MM-DD'),
			#{prcComNo},
			#{prcComDiv},
			#{prdComDNote}
		);
	
	if #{prdPlanDNum} is not null then
		select prd_work_vol into v_prdWorkVol
		from produce_plan_d
		where prd_plan_d_num = #{prdPlanDNum};
		
		select sum(prd_com_vol) into v_prdComVol
		from produce_command_d
		where prd_plan_d_num = #{prdPlanDNum} and prc_com_div = '정상';
							
		if v_prdWorkVol <= v_prdComVol then
			update produce_plan_d 
			set com_chk = 'Y'
			where prd_plan_d_num = #{prdPlanDNum};
		end if;
		
	end if;
	
	end;

mapper.update:
declare
	v_prdWorkVol produce_plan_d.prd_work_vol%type;
	v_prdComVol produce_command_d.prd_com_vol%type;
	v_ordIndVol order_d.ord_ind_vol%type;
	begin
		update Produce_Command_D set 
			itm_code = #{itmCode},
			prd_com_vol = #{prdComVol},
			prd_com_d_date = to_date(#{prdComDDate1},'YYYY-MM-DD'),
			prc_com_no = #{prcComNo},
			prc_com_div = #{prcComDiv},
			prd_com_d_note = #{prdComDNote}
		where prd_com_d_num = #{prdComDNum};
		
		if #{prdPlanDNum} is not null then
			select prd_work_vol into v_prdWorkVol
			from produce_plan_d
			where prd_plan_d_num = #{prdPlanDNum};
			
			select nvl(sum(prd_com_vol),0) into v_prdComVol
			from produce_command_d
			where prd_plan_d_num = #{prdPlanDNum} and prc_com_div = '정상';
								
			if v_prdWorkVol > v_prdComVol then
				update produce_plan_d 
				set com_chk = 'N'
				where prd_plan_d_num = #{prdPlanDNum};
			else
				update produce_plan_d 
				set com_chk = 'Y'
				where prd_plan_d_num = #{prdPlanDNum};
			end if;
			
		end if;
		
	end;

mapper.delete:
declare
	v_prdWorkVol produce_plan_d.prd_work_vol%type;
	v_prdComVol produce_command_d.prd_com_vol%type;
	v_ordIndVol order_d.ord_ind_vol%type;
begin
	delete from Produce_Command_D 
	where prd_com_d_num = #{prdComDNum};
	
	if #{prdPlanDNum} is not null then
		select prd_work_vol into v_prdWorkVol
		from produce_plan_d
		where prd_plan_d_num = #{prdPlanDNum};
		
		select sum(prd_com_vol) into v_prdComVol
		from produce_command_d
		where prd_plan_d_num = #{prdPlanDNum} and prc_com_div = '정상';
							
		if v_prdWorkVol > v_prdComVol then
			update produce_plan_d 
			set com_chk = 'N'
			where prd_plan_d_num = #{prdPlanDNum};
		end if;
		
	end if;
	
end;

mapper.update:
declare
	v_ordIndVol order_d.ord_ind_vol%type;
begin
	select ord_ind_vol into v_ordIndVol 
	from order_d
	where ord_d_num = #{ordDNum};

	update order_d set
	ord_ind_vol = v_ordIndVol + #{prdComVol}
	where ord_d_num = #{ordDNum};
end;

declare
	v_ordIndVol order_d.ord_ind_vol%type;
	v_prdComVol produce_command_d.prd_com_vol%type;
begin
	select ord_ind_vol into v_ordIndVol 
	from order_d
	where ord_d_num = #{ordDNum};
	
	select prd_com_vol into v_prdComVol
	from produce_command_d
	where prd_com_d_num = #{prdComDNum};

	update order_d set
	ord_ind_vol = v_ordIndVol - v_prdComVol + #{prdComVol}
	where ord_d_num = #{ordDNum};
end;

 
		declare
			v_ordIndVol order_d.ord_ind_vol%type;
		begin
			select ord_ind_vol into v_ordIndVol 
			from order_d
			where ord_d_num = #{ordDNum};
		
			update order_d set
			ord_ind_vol = v_ordIndVol - #{prdComVol}
			where ord_d_num = #{ordDNum};
		end;

update produce_command_mat set 
lot_num = #{lotNum},
mat_vol = #{matVol},
prd_com_mat_O = #{prdComMatO},
prd_com_mat_note = #{prdComMatNote}
where prd_com_mat_num = #{prdComMatNum}

mapper.delete:
begin
	if #{prdComMatNum} is not null then
		delete from produce_command_mat
		where prd_com_mat_num = #{prdComMatNum};
	else
		delete from produce_command_mat
		where prd_com_d_num = #{prdComDNum};
	end if;
end;

mapper.update:
 
		declare
			v_matVol material_stock.mat_vol%type;
		begin
			IF #{lotNum} is null THEN
		      	SELECT mat_vol into v_matVol FROM material_stock  
	        	where mat_code = #{matCode} and lot_num is null;
		    ELSE
		     	SELECT mat_vol into v_matVol FROM material_stock  
	        	where mat_code = #{matCode} and lot_num = #{lotNum};
		    END IF;
	        
			update material_stock set 
			mat_vol = v_matVol - #{matVol}
			where lot_num = #{lotNum} and mat_code = #{matCode};
		end;

 
		update Produce_Command_D set 
			mat_out_chk = 'Y'
		where prd_com_d_num = #{prdComDNum}
