mapper.insert:

	INSERT INTO PRODUCE_COMMAND_D 
		( PRD_COM_D_NUM
		  , PRD_COM_NUM
		  , MAT_CODE
		  , LOT_NUM
		  , ITM_CODE
		  , PRC_F_NO
		  , PRD_COM_VOL
		  , PRD_COM_D_DATE
		  , PRC_COM_NO
		  , PRC_COM_DIV
		  , MAT_VOL
		  , PRD_COM_NOTE
		  , DIV_MAT_CODE
		  , DIV_LOT_NUM
		  , PRD_PLAN_NUM )
	VALUES ( #{prdComDNum}
		  , #{prdComNum}
		  , #{matCode}
		  , #{lotNum}
		  , #{itmCode}
		  , #{prcFNo}
		  , #{prdComVol}
		  , #{prdComDDate}
		  , #{prcComNo}
		  , #{prcComDiv}
		  , #{matVol}
		  , #{prdComNote}
		  , #{divMatCode}
		  , #{divLotNum}
		  , #{prdPlanNum} )

mapper.update:

	UPDATE PRODUCE_COMMAND_D
	SET PRD_COM_D_NUM=#{prdComDNum}
		, PRD_COM_NUM=#{prdComNum}
		, MAT_CODE=#{matCode}
		, LOT_NUM=#{lotNum}
		, ITM_CODE=#{itmCode}
		, PRC_F_NO=#{prcFNo}
		, PRD_COM_VOL=#{prdComVol}
		, PRD_COM_D_DATE=#{prdComDDate}
		, PRC_COM_NO=#{prcComNo}
		, PRC_COM_DIV=#{prcComDiv}
		, MAT_VOL=#{matVol}
		, PRD_COM_NOTE=#{prdComNote}
		, DIV_MAT_CODE=#{divMatCode}
		, DIV_LOT_NUM=#{divLotNum}
		, PRD_PLAN_NUM=#{prdPlanNum}
				WHERE PRD_COM_D_NUM=#{prdComDNum}

mapper.select:
		select p.prd_date, m.OPER_NAME, d.ITM_CODE,i.ITM_NAME, d.ORD_NUM,
		m.ORD_DELIVERY_DATE
		,d.ORD_VOL,p.prd_work_vol,p.prd_plan_date,p.prd_no,p.prd_d_note
		from ORDER_D d, ORDER_M m,
		(select * from
		PRODUCE_PLAN p, PRODUCE_PLAN_d d
		where p.PRD_PLAN_NUM = d.PRD_NUM) p, ITEM i
		where p.ORD_NUM = d.ord_num
		and d.ORD_NUM = m.ORD_NUM
		and d.ITM_CODE = i.ITM_CODE;
		
					
					and Prd_Com_D_Num < #{prdComDNum}
				
		
		
			AND
				PRD_COM_D_NUM = #{searchKeyword}
			
			AND
				PRD_COM_NUM LIKE '%' || #{searchKeyword} || '%'
			
		
		ORDER BY
		PRD_COM_D_NUM DESC
		
							
	) A WHERE ROWNUM <= 10
)

SELECT COUNT(*) totcnt FROM PRODUCE_COMMAND_D WHERE 1=1
AND PRD_COM_D_NUM = #{searchKeyword}
AND PRD_COM_NUM LIKE '%' || #{searchKeyword} || '%'
