mapper.insert:

	INSERT INTO PRODUCE_PLAN_D 
		( PRD_PLAN_D_NUM
		  , PRD_NUM
		  , ITM_CODE
		  , PRD_WORK_VOL
		  , PRD_PLAN_DATE
		  , PRD_NO
		  , PRD_NOTE
		  , COM_CHK
		  , ORD_NUM )
	VALUES ( #{prdPlanDNum}
		  , #{prdNum}
		  , #{itmCode}
		  , #{prdWorkVol}
		  , #{prdPlanDate}
		  , #{prdNo}
		  , #{prdNote}
		  , #{comChk}
		  , #{ordNum} )

mapper.update:

	UPDATE PRODUCE_PLAN_D
	SET PRD_PLAN_D_NUM=#{prdPlanDNum}
		, PRD_NUM=#{prdNum}
		, ITM_CODE=#{itmCode}
		, PRD_WORK_VOL=#{prdWorkVol}
		, PRD_PLAN_DATE=#{prdPlanDate}
		, PRD_NO=#{prdNo}
		, PRD_NOTE=#{prdNote}
		, COM_CHK=#{comChk}
		, ORD_NUM=#{ordNum}
				WHERE PRD_PLAN_D_NUM=#{prdPlanDNum}

mapper.select:
SELECT * FROM (
	SELECT A.*, ROWNUM RNUM FROM (
				SELECT *
				FROM pd
				WHERE PRD_NUM = #{prdNum} 
				ORDER BY 
				PRD_PLAN_D_NUM DESC
		
							
	) A WHERE ROWNUM <= 10
)
