mapper.update:
		
		declare
			v_num number;
			v_mov_num process_result.mov_num%type;
			v_mov_num_v process_result.mov_num%type;
			v_prc_state process_result_d.prc_state%type;
            v_res_vol process_result_d.prc_res_vol%type;
            v_prd_com_d_num produce_command_d.prd_com_d_num%type;
            v_total_res_vol process_result_d.prc_res_vol%type :=0;
            v_count number :=0;
			begin
			
				update process_result_d
				set prc_end_time = to_date(#{prcEndTime},'yyyy-MM-dd:HH24:mi:ss')
					, PRC_STATE = '완료'
				where prc_res_d_num = #{prcResDNum}; 
			
				select mov_num
				into v_mov_num_v
				from process_result_d
				where prc_res_d_num = #{prcResDNum}; 
				
				select prd_com_d_num
				 into v_prd_com_d_num
			    from process_result
			    where mov_num = v_mov_num_v;
               
                
                
			    select count(*) 
			    into v_num
			    from process_result
			    where prd_com_d_num = v_prd_com_d_num; 
                
			    for i in 1..v_num loop
	        
			        select mov_num
			        into v_mov_num
			        from (
			                select mov_num, rownum as rnum
			                from process_result
			                where prd_com_d_num = v_prd_com_d_num) A
			        where a.rnum =i;
	        
			        select prc_state,prc_res_vol
			        into v_prc_state,v_res_vol
			        from process_result_d
			        where prc_res_no =(
			                            select max(prc_res_no) 
			                            from process_result_d
			                            where mov_num = v_mov_num) 
			        and mov_num = v_mov_num;
	        
	        
				    if v_prc_state = '완료' then
				    
                   		v_total_res_vol := v_total_res_vol + v_res_vol;
                   
                   		v_count := v_count+1;
                   		
				    end if;
				    
	    		end loop;
	    			
	    		update process_result 
                set prc_bck_vol = v_total_res_vol
                where mov_num =v_mov_num;
                   
                if v_num = v_count then
                	update produce_command_d
                	set prc_com_y = 'Y'
                	where prd_com_d_num = (select prd_com_d_num
                	                        from process_result 
                	                        where mov_num = v_mov_num);
                	
                     
                end if;
                        
	    		 
    
		end;

		update process_result_d
		set prc_str_time = to_date(#{prcStrTime},'yyyy-MM-dd:HH24:mi:ss')
		where prc_res_d_num = #{prcResDNum}

declare v_res_no process_result_d.prc_res_no%type; v_mov_num process_result_d.mov_num%type; begin select prc_res_no, mov_num into v_res_no, v_mov_num from process_result_d where prc_res_d_num = #{prcResDNum};
update process_result_d set prc_str_time = null, mac_code = null, emp_id = null, prc_res_vol = 0, prc_err_vol = 0, prc_result_d_note = null where prc_res_d_num = #{prcResDNum};
update process_result_d set prc_com_d_vol = 0 where prc_res_no = v_res_no+1 and mov_num = v_mov_num; end;

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select to_char(pcd.prd_com_d_date ,'yyyy-MM-dd') as prd_com_d_date ,pcd.prd_com_num ,pcd.prd_com_d_num ,pcd.itm_code ,item.itm_name ,p.prc_name ,prd.prc_res_no ,prd.prc_com_d_vol ,prd.prc_res_vol ,(prd.prc_com_d_vol-prd.prc_res_vol) as prc_n_vol ,pcd.prc_com_div ,prd.prc_res_d_num ,prd.prc_code ,prd.emp_id ,prd.mac_code ,pcd.ord_d_num from produce_command_d pcd , process_result pr , process_result_d prd , item , process p where pr.prd_com_d_num = pcd.prd_com_d_num and item.itm_code=pcd.itm_code and pr.mov_num =prd.mov_num and p.prc_code = prd.prc_code and
prd.prc_com_d_vol > 0 and pcd.prc_com_y <> 'Y'
and prd.prc_code =${prcCode}
and prd.prc_res_d_num < #{prcResDNum1}
ORDER BY prd.prc_res_d_num DESC
) A WHERE ROWNUM <=10 )

mapper.update:
	declare
		    v_old_res_vol process_result_d.prc_res_vol%type;
		    v_old_err_vol process_result_d.prc_err_vol%type;
		    v_mov_num process_result_d.mov_num%type;
		    v_prc_res_no process_result_d.prc_res_no%type;

		    v_cha_res_vol number := ${prcResVol};
		    v_cha_err_vol number := ${prcErrVol};
		    
		    v_test number;
		    v_last_prc_res_no process_result_d.prc_res_no%type;
	begin
			select max(prc_res_no) 
			into v_last_prc_res_no 
			from process_result_d
			where mov_num = #{movNum};
				
			update process_result_d 
			set emp_id = #{empId},
				mac_code = #{macCode},
				prc_work_num = #{prcWorkNum}
			where prc_res_d_num = #{prcResDNum};
			
			update machine
			set worker = (select emp_name from employees where emp_id = #{empId})
			where mac_code = #{macCode};
			
		    select prc_res_vol , prc_err_vol   , mov_num   , prc_res_no
		    into v_old_res_vol , v_old_err_vol , v_mov_num ,v_prc_res_no
		    from process_result_d
		    where prc_res_d_num=#{prcResDNum};
		    
		    
		    update process_result_d
		    set prc_res_vol = v_cha_res_vol
		    , prc_err_vol = v_cha_err_vol
		    where prc_res_d_num = #{prcResDNum};
		    
		    select count(*) 
		        into v_test
		        from item_stock
		        where itm_stc_num = #{lotNum};
		    
		    
		    if v_prc_res_no < v_last_prc_res_no then
		    
		    update process_result_d
		    set prc_com_d_vol = prc_com_d_vol + v_cha_res_vol - v_old_res_vol
		    where mov_num = #{movNum} and
		          prc_res_no = ${prcResNo}+1;
		    end if;
		    
      
		    if v_prc_res_no = v_last_prc_res_no then
		      
		      if v_test =0 then
		         insert into item_stock
		         values(#{lotNum},#{itmCode},(v_cha_res_vol-v_old_res_vol)
		         );
		      else
		      	update item_stock
		      	set itm_vol = itm_vol+(v_cha_res_vol-v_old_res_vol)
		      	where ITM_STC_NUM = #{lotNum};
		      end if; 
		      
		      update process_result
		      set PRC_BCK_VOL = PRC_BCK_VOL+(v_cha_res_vol-v_old_res_vol)
		      where mov_num = #{movNum};
		      
		
		    end if;

	end;

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select pcd.prd_com_num ,item.itm_name , pr.itm_code , pr.mov_num ,pcd.prd_com_vol ,pr.prc_com_vol ,pr.prc_bck_vol ,to_char(pcd.prd_com_d_date,'yyyy-MM-dd') as prd_com_d_date ,pcd.prc_com_div ,pcd.prc_com_y
from process_result pr , produce_command_d pcd , item
where pr.prd_com_d_num = pcd.prd_com_d_num and pr.itm_code = item.itm_code
and mat_out_chk = 'Y'
and pr.mov_num < #{movNum1}
and
pcd.prd_com_d_date >= to_date(#{bDate} ,'yyyy-MM-dd') and pcd.prd_com_d_date <= to_date(#{aDate} ,'yyyy-MM-dd')
and pcd.prc_com_y = #{prcComY}
order by pr.mov_num
) A WHERE ROWNUM <=10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select prd.mov_num ,prd.prc_res_no ,prd.prc_code ,p.prc_name ,m.mac_name ,prd.prc_com_d_vol ,prd.prc_res_vol ,prd.prc_err_vol ,to_char(prd.prc_str_time,'HH24:mi:ss') as prc_str_time ,to_char(prd.prc_end_time,'HH24:mi:ss') as prc_end_time ,prd.prc_result_d_note from process_result_d prd , process p , machine m where prd.prc_code = p.prc_code and m.mac_code = prd.mac_code
and prd.prc_res_d_num < #{prcResDNum}
and prd.mov_num = #{movNum} order by prd.prc_res_d_num
) A WHERE ROWNUM <=10 )
