mapper.insert:
		
			declare
				ord varchar(50) :='SO'||to_char(sysdate,'yyMMdd')||to_char(order_num_seq.nextval,'FM0000');
				vol number;
			begin
 				select itm_vol into vol
 				from item_stock
 				where itm_code = #{itmCode};
 				
 				INSERT INTO ORDER_M 
					VALUES ( ord
				  			, sysdate
				  			, #{ordDeliveryDate}
				  			, #{operCode}
				  			, #{operName} );
				insert into order_d
    				values
          					(order_d_seq.nextval
          					,ord
          					, #{itmCode}
          					,'진행'
          					, #{ordVol}
          					,0
          					,0
          					,vol
          					, #{ordNote}
          					,'N');
					end;

mapper.update:

	UPDATE ORDER_M
	SET ORD_NUM=#{ordNum}
		, ORD_REQUEST_DATE=#{ordRequestDate}
		, ORD_DELIVERY_DATE=#{ordDeliveryDate}
		, OPER_CODE=#{operCode}
		, OPER_NAME=#{operName}
				WHERE ORD_NUM=#{ordNum}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select m.ord_num , to_char(m.ORD_DELIVERY_DATE,'yyyy-MM-dd') as ORD_DELIVERY_DATE , m.oper_name , d.ord_d_num , item.ITM_name , d.ORD_VOL , d.ORD_OUT_VOL from order_d d, order_m m,item where m.ORD_NUM=d.ORD_NUM and d.ord_status ='진행' and item.itm_code = d.itm_code;
and d.ord_d_num < ${ordDNum}
ORDER BY d.ORD_D_NUM DESC
) A WHERE ROWNUM <= 10 )

SELECT COUNT(*) totcnt FROM ORDER_M WHERE 1=1
AND ORD_NUM = #{searchKeyword}
AND ORD_REQUEST_DATE LIKE '%' || #{searchKeyword} || '%'
