mapper.update:
		declare
			oldVol number;
			changeVol number;
		begin
			    select itm_vol 
			    into oldVol
			    from item_history_d
			    where itm_his_d_num = #{itmHisDNum};

			    
			    changeVol := #{itmVol}-oldVol;
			    
			    update item_history_d
					set 
					    itm_vol = #{itmVol}
					   ,itm_code = #{itmCode}
					   ,lot_num = #{lotNum}
					   ,itm_price = #{itmPrice}
			        where itm_his_d_num = #{itmHisDNum};
			        
			    update order_d
			    set ord_out_vol = ord_out_vol+changeVol
			    where ord_num = #{ordNum} and
			    itm_code = #{itmCode};
			    
			    update item_stock
			    set itm_vol = itm_vol - changeVol
			    where itm_stc_num = #{lotNum} and
			    itm_code = #{itmCode};
			
			
			end;

mapper.insert:
			declare
			v_ord_vol order_d.ord_vol%type;
            v_ord_out_vol order_d.ord_out_vol%type;
            
			begin
			    
			   select ord_vol, ord_out_vol
			   into v_ord_vol, v_ord_out_vol
			   from order_d
			   where ord_num =#{ordNum}
			   and itm_code = #{itmCode};
				      
				insert into item_history_d
    				values
          					(item_history_d_seq.nextval
          					, #{itmHisNum}
          					, #{itmCode}
          					, #{itmVol}
          					, #{lotNum}
          					, #{itmPrice}
          					, #{itmNoteD});
          					
          		
          		update item_stock 
        			set itm_vol = itm_vol- #{itmVol}
       					 where 	itm_stc_num = #{lotNum}
        						and   itm_code = #{itmCode};
        						
        		if v_ord_out_vol + #{itmVol} = v_ord_vol then
          			update order_d
        			set ord_out_vol = ord_out_vol+#{itmVol},
        			    ord_status = '완료'
        				where ord_num =  #{ordNum} 
        				and itm_code = #{itmCode};
          		
          		else
          			update order_d
        			set ord_out_vol = ord_out_vol+#{itmVol}
        				where ord_num =  #{ordNum} 
        				and itm_code = #{itmCode};
          			
          		end if;
			end;

			declare
				v_ord_vol order_d.ord_vol%type;
	            v_ord_out_vol order_d.ord_out_vol%type;
				v_count number;
			begin
			   select ord_vol, ord_out_vol
			   into v_ord_vol, v_ord_out_vol
			   from order_d
			   where ord_num =#{ordNum}
			   and itm_code = #{itmCode};
				
				select count(*)
			    into v_count 
			    from item_history
			    where itm_his_num = #{itmHisNum};
			    
			    if v_count = 0 then
			      INSERT INTO item_history 
					VALUES ( #{itmHisNum}
				  			, #{ordNum}
				  			, '출고'
				  			,to_date(to_char(#{itmHisRdy},'yyyy-MM-dd'),'yyyy-MM-dd')
                			, #{itmNote});
			    end if;
			
				select ord_vol, ord_out_vol 
				into v_ord_vol, v_ord_out_vol
				from order_d
				where ord_num = #{ordNum} and
				      itm_code = #{itmCode};
			
				insert into item_history_d
    				values
          					(item_history_d_seq.nextval
          					, #{itmHisNum}
          					, #{itmCode}
          					, #{itmVol}
          					, #{lotNum}
          					, #{itmPrice}
          					, #{itmNoteD});
        		update item_stock 
        		set itm_vol = itm_vol- #{itmVol}
       					 where 	itm_stc_num = #{lotNum}
        						and   itm_code = #{itmCode};
        
        		if v_ord_out_vol + #{itmVol} = v_ord_vol then
          			update order_d
        			set ord_out_vol = ord_out_vol+#{itmVol},
        			    ord_status = '완료'
        				where ord_num =  #{ordNum} 
        				and itm_code = #{itmCode};
          		
          		else
          			update order_d
        			set ord_out_vol = ord_out_vol+#{itmVol}
        				where ord_num =  #{ordNum} 
        				and itm_code = #{itmCode};
          		end if;
					end;

mapper.delete:
		
			declare
				itmVol item_history_d.itm_vol%type := #{itmVol};
				v_status order_d.ord_status%type;
				
			begin
				
				
				select ord_status
				into v_status
				from order_d
				where ord_num = #{ordNum} and itm_code = #{itmCode};
			
				DELETE FROM ITEM_HISTORY_D
						WHERE ITM_HIS_D_NUM=#{itmHisDNum};
                
                
        	update item_stock 
        		set itm_vol = itm_vol+ #{itmVol}
       					 where 	itm_stc_num = #{lotNum}
        						and   itm_code = #{itmCode};
	        if v_status = '진행' then
	        		update order_d
	        		set ord_out_vol = ord_out_vol-#{itmVol}
	        		where ord_num =  #{ordNum} 
	        				and itm_code = #{itmCode};
	        else	
	        		update order_d
	        		set ord_out_vol = ord_out_vol-#{itmVol}
	        						, ord_status='진행'
	        		where ord_num =  #{ordNum} 
	        				and itm_code = #{itmCode};
	        end if
					end;

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select to_char(i.itm_his_rdy+1,'yyyy-MM-dd') as itm_his_rdy ,od.oper_name , id.lot_num ,id.itm_code ,item.itm_name ,od.ord_num ,id.itm_vol ,id.itm_price ,(id.itm_vol*id.itm_price) as total_price , id.itm_note_d , id.itm_his_d_num , i.itm_note ,i.itm_his_num ,i.itm_div from item_history i , item_history_d id ,order_m od, item where i.itm_his_num = id.itm_his_num and i.ord_num = od.ord_num and i.itm_div ='출고' and id.itm_code = item.itm_code
and id.ITM_HIS_D_NUM < #{itmHisDNum}
and
i.ITM_HIS_RDY >= to_date(#{bDate},'yyyy-MM-dd') and i.ITM_HIS_RDY <= to_date(#{aDate},'yyyy-MM-dd')
and od.oper_code in
#{item}
and id.itm_code in (${itmCode})
ORDER BY id.itm_his_d_num DESC
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select to_char(i.itm_his_rdy+1,'yyyy-MM-dd') as itm_his_rdy , i.itm_his_num , o.oper_code , o.oper_name , i.itm_note , id.itm_code , item.itm_name , item.itm_size , item.itm_unit , o.ord_num , od.ord_vol , od.ord_out_vol , id.itm_vol , (od.ord_vol- ord_out_vol) as itm_nout_vol , its.itm_stock , id.lot_num , id.itm_price , (id.itm_vol*id.itm_price) total_price , id.itm_note_d , id.itm_his_d_num from item_history i, item_history_d id, order_m o, order_d od, item, (select sum(itm_vol) as itm_stock, itm_code from item_stock group by itm_code) its
where i.itm_his_num = id.itm_his_num and i.ord_num = o.ord_num and id.itm_code = item.itm_code and i.ord_num = od.ord_num and item.itm_code = its.itm_code and item.itm_code = od.itm_code
and o.ord_num = #{ordNum}
and id.ITM_HIS_D_NUM < #{itmHisDNum1}
and
i.ITM_HIS_RDY = to_date(#{bDate} ,'yyyy-MM-dd')
and od.oper_code = #{operCode}
and id.itm_his_num = #{itmHisNum}
ORDER BY id.itm_his_d_num desc
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select to_char(i.itm_his_rdy+1,'yyyy-MM-dd') as itm_his_rdy ,i.itm_his_num ,o.oper_name ,item.itm_name ,i.itm_note from item_history i, order_m o, order_d d, item, item_history_d ihd where i.ord_num = o.ord_num and o.ord_num = d.ord_num and d.itm_code = item.itm_code and d.ord_status = '진행' and i.itm_his_num = ihd.itm_his_num and ihd.itm_code = item.itm_code
and i.itm_his_num < #{itmHisNum1}
and
i.ITM_HIS_RDY >= to_date(#{bDate} ,'yyyy-MM-dd') and i.ITM_HIS_RDY <= to_date(#{aDate} ,'yyyy-MM-dd')
ORDER BY i.itm_his_num desc
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select od.ord_num , od.itm_code , item.itm_name , item.itm_size , item.itm_unit , od.ord_vol , od.ord_out_vol , (od.ord_vol-od.ord_out_vol) as itm_nout_vol from order_d od ,item where od.itm_code = item.itm_code and od.ord_num = #{ordNum}
and item.itm_code < #{itmCode1}
ORDER BY item.itm_code
) A WHERE ROWNUM <= 10 )
