mapper.insert:
		
			declare
				itmnum varchar(50) :='ITH'||to_char(sysdate,'yyMMdd')||to_char(item_his_seq.nextval,'FM0000');
			begin
 				INSERT INTO item_history 
					VALUES ( itmnum
				  			, #{ordNum}
				  			, '출고'
				  			, sysdate
                			, #{itmNote});
				insert into item_history_d
    				values
          					(item_history_d_seq.nextval
          					, itmnum
          					, #{itmCode}
          					, ${itmVol}
          					, #{lotNum}
          					, #{itmPrice}
          					, #{itmNoteD});
        		update item_stock 
        		set itm_vol = itm_vol- ${itmVol}
       					 where 	itm_stc_num = #{lotNum}
        						and   itm_code = #{itmCode};
        
        		update order_d
        			set ord_out_vol = ord_out_vol+${itmVol}
        				where ord_num = #{ordNum} 
        				and itm_code = #{itmCode};
          					
					end;

mapper.update:

	UPDATE ITEM_HISTORY
	SET ITM_HIS_NUM=#{itmHisNum}
		, ORD_NUM=#{ordNum}
		, ITM_DIV=#{itmDiv}
		, ITM_HIS_RDY=#{itmHisRdy}
		, ITM_NOTE=#{itmNote}
				WHERE ITM_HIS_NUM=#{itmHisNum}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select i.ITM_HIS_NUM , i.ORD_NUM , i.ITM_DIV , to_char(i.ITM_HIS_RDY,'yyyy-MM-dd') as ITM_HIS_RDY , i.ITM_NOTE , d.ITM_HIS_D_NUM , d.ITM_CODE , d.ITM_VOL , d.LOT_NUM , d.ITM_PRICE , (d.ITM_VOL * d.ITM_PRICE) as total_price , d.ITM_NOTE_d from item_history_d d , ITEM_HISTORY i
,(select ord_num from order_m where oper_code like '%' || #{operCode} || '%') o
where i.ITM_HIS_NUM = d.ITM_HIS_NUM
and i.ORD_NUM = o.ord_num
and d.ITM_HIS_D_NUM < #{itmHisDNum}
and i.ORD_NUM = #{ordNum}
and
to_date(i.ITM_HIS_RDY,'yyyy-MM-dd') >= to_date(#{bDate} ,'yyyy-MM-dd') and to_date(i.ITM_HIS_RDY,'yyyy-MM-dd') <= to_date(#{aDate} ,'yyyy-MM-dd')
ORDER BY i.ITM_HIS_NUM DESC
) A WHERE ROWNUM <= 10 )

SELECT COUNT(*) totcnt FROM ITEM_HISTORY WHERE 1=1
AND ITM_HIS_NUM = #{searchKeyword}
AND ORD_NUM LIKE '%' || #{searchKeyword} || '%'
