mapper.update:
		declare
			oldVol number;
			changeVol number;
		begin
			    select itm_vol into oldVol
			    from item_history_d
			    where itm_his_d_num = #{itmHisDNum};

			    
			    changeVol := #{itmVol}-oldVol;
			    
			    update item_history_d
					set 
					    itm_vol = #{itmVol}
					   ,itm_code = #{itmCode}
					   ,lot_num = #{lotNum}
					   ,itm_price = #{itmPrice}
			        where itm_his_d_num = #{itmHisDNum};
			        
			    update order_d
			    set ord_out_vol = ord_out_vol+changeVol
			    where ord_num = #{ordNum} and
			    itm_code = #{itmCode};
			    
			    update item_stock
			    set itm_vol = itm_vol - changeVol
			    where itm_stc_num = #{lotNum} and
			    itm_code = #{itmCode};
			
			end;

mapper.insert:
			declare
			begin
				insert into item_history_d
    				values
          					(item_history_d_seq.nextval
          					, #{itmHisNum}
          					, #{itmCode}
          					, #{itmVol}
          					, #{lotNum}
          					, #{itmPrice}
          					, #{itmNoteD});
        		update item_stock 
        		set itm_vol = itm_vol- #{itmVol}
       					 where 	itm_stc_num = #{lotNum}
        						and   itm_code = #{itmCode};
        
        		update order_d
        			set ord_out_vol = ord_out_vol+#{itmVol}
        				where ord_num =  #{ordNum} 
        				and itm_code = #{itmCode};	
					end;

			begin
				insert into item_history_d
    				values
          					(item_history_d_seq.nextval
          					, #{itmHisNum}
          					, #{itmCode}
          					, #{itmVol}
          					, #{lotNum}
          					, #{itmPrice}
          					, #{itmNoteD});
        		update item_stock 
        		set itm_vol = itm_vol- #{itmVol}
       					 where 	itm_stc_num = #{lotNum}
        						and   itm_code = #{itmCode};
        
        		update order_d
        			set ord_out_vol = ord_out_vol+#{itmVol}
        				where ord_num =  #{ordNum} 
        				and itm_code = #{itmCode};	
					end;

mapper.delete:
		
			declare
				itmVol item_history_d.itm_vol%type := #{itmVol};
				
			begin
				DELETE FROM ITEM_HISTORY_D
						WHERE ITM_HIS_D_NUM=#{itmHisDNum};
                
                
        	update item_stock 
        		set itm_vol = itm_vol+ #{itmVol}
       					 where 	itm_stc_num = #{lotNum}
        						and   itm_code = #{itmCode};
        
        		update order_d
        			set ord_out_vol = ord_out_vol-#{itmVol}
        				where ord_num =  #{ordNum} 
        				and itm_code = #{itmCode};	
					end;

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select to_char(i.itm_his_rdy+1,'yyyy-MM-dd') as itm_his_rdy ,od.oper_name , id.lot_num ,id.itm_code ,item.itm_name ,od.ord_num ,id.itm_vol ,id.itm_price ,(id.itm_vol*id.itm_price) as total_price , id.itm_note_d , id.itm_his_d_num , i.itm_note ,i.itm_his_num ,i.itm_div from item_history i , item_history_d id ,order_m od, item where i.itm_his_num = id.itm_his_num and i.ord_num = od.ord_num and i.itm_div ='출고' and id.itm_code = item.itm_code
and id.ITM_HIS_D_NUM < #{itmHisDNum}
and
i.ITM_HIS_RDY >= to_date(#{bDate},'yyyy-MM-dd') and i.ITM_HIS_RDY <= to_date(#{aDate},'yyyy-MM-dd')
and od.oper_name = #{operName}
and id.itm_code = #{itmCode}
ORDER BY id.itm_his_d_num DESC
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select to_char(i.itm_his_rdy+1,'yyyy-MM-dd') as itm_his_rdy , i.itm_his_num , o.oper_code , o.oper_name , i.itm_note , id.itm_code , item.itm_name , item.itm_size , item.itm_unit , o.ord_num , od.ord_vol , (select sum(id.itm_vol) from item_history_d id, item_history i where id.itm_his_num = i.itm_his_num group by i.ord_num) as ord_out_vol , id.itm_vol , (od.ord_vol-(select sum(id.itm_vol) from item_history_d id, item_history i where id.itm_his_num = i.itm_his_num group by i.ord_num)-id.itm_vol) as itm_nout_vol , its.itm_vol as itm_stock , id.lot_num , id.itm_price , (id.itm_vol*id.itm_price) total_price , id.itm_note_d , id.itm_his_d_num from item_history i, item_history_d id, order_m o, order_d od, item, item_stock its where i.itm_his_num = id.itm_his_num and i.ord_num = o.ord_num and i.ord_num = od.ord_num and item.itm_code = its.itm_code and item.itm_code = od.itm_code
and id.ITM_HIS_D_NUM < #{itmHisDNum}
and
i.ITM_HIS_RDY = to_date(#{bDate} ,'yyyy-MM-dd')
and od.oper_code = #{operCode}
and id.itm_code = #{itmCode}
and id.itm_his_num IN ('${str}')
ORDER BY id.itm_his_d_num desc
) A WHERE ROWNUM <= 10 )

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( select to_char(i.itm_his_rdy+1,'yyyy-MM-dd') as itm_his_rdy ,i.itm_his_num ,o.oper_name ,i.itm_note ,rownum as ITM_HIS_D_NUM from item_history i, order_m o where i.ord_num = o.ord_num
and rownum < #{itmHisDNum}
and
i.ITM_HIS_RDY >= to_date(#{bDate} ,'yyyy-MM-dd') and i.ITM_HIS_RDY <= to_date(#{aDate} ,'yyyy-MM-dd')
ORDER BY i.itm_his_rdy desc
) A WHERE ROWNUM <= 10 )

SELECT COUNT(*) totcnt FROM ITEM_HISTORY WHERE 1=1
AND ITM_HIS_NUM = #{searchKeyword}
AND ORD_NUM LIKE '%' || #{searchKeyword} || '%'
